document.addEventListener("DOMContentLoaded",function(){window.toggleMobileMenu=function(){const mobileMenu=document.getElementById("mobileMenu");mobileMenu.classList.toggle("active");};window.closeMobileMenu=function(){const mobileMenu=document.getElementById("mobileMenu");mobileMenu.classList.remove("active");};document.querySelectorAll("a[href^=\"#\"]").forEach(anchor=>{anchor.addEventListener("click",function(e){e.preventDefault();const target=document.querySelector(this.getAttribute("href"));if(target){target.scrollIntoView({behavior: "smooth"});}});});window.toggleDropdown=function(id){const dropdown=document.getElementById(id);const icon=document.getElementById(id+"-icon");if(dropdown){dropdown.classList.toggle("active");if(icon){icon.classList.toggle("rotated");}}};window.togglePackagesSection=function(targetId){const constructionPackages=document.getElementById("construction-packages-section");const interiorPackages=document.getElementById("interior-packages-section");constructionPackages.classList.add("hidden");interiorPackages.classList.add("hidden");const targetSection=document.getElementById(targetId);if(targetSection){targetSection.classList.remove("hidden");setTimeout(()=>{targetSection.scrollIntoView({behavior: "smooth",block: "start"});},100);}};document.querySelectorAll(".view-packages-btn").forEach(button=>{button.addEventListener("click",function(){const targetId=this.dataset.target;window.togglePackagesSection(targetId);});});window.handleFormSubmit=function(event){event.preventDefault();const formData=new FormData(event.target);const name=formData.get('name');const email=formData.get('email');const phone=formData.get('phone');const service=formData.get('service');const message=formData.get('message');const emailBody=`Name: ${name}%0D%0AEmail: ${email}%0D%0APhone: ${phone}%0D%0AService: ${service}%0D%0AMessage: ${message}`;window.location.href=`mailto:sales@aneliadesign.com?subject=Website Inquiry from ${name}&body=${emailBody}`;};window.addEventListener('scroll',function(){const navbar=document.querySelector('.navbar');if(window.scrollY>50){navbar.style.background='rgba(255,255,255,0.95)';}else{navbar.style.background='rgba(255,255,255,0.8)';}});const images=document.querySelectorAll('img');images.forEach(img=>{img.addEventListener('load',function(){this.style.opacity='1';});img.style.opacity='0';img.style.transition='opacity 0.3s ease';if(img.complete){img.style.opacity='1';}});const observerOptions={threshold: 0.1,rootMargin: '0px 0px-50px 0px'};const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.style.opacity='1';entry.target.style.transform='translateY(0)';}});},observerOptions);const animateElements=document.querySelectorAll('.feature-card,.service-card,.package-card,.project-card');animateElements.forEach(el=>{el.style.opacity='0';el.style.transform='translateY(20px)';el.style.transition='opacity 0.6s ease,transform 0.6s ease';observer.observe(el);});});